mapper.update:
UPDATE firstpaper

	
		tea_status=#{map.teaStatus},
	
	
		leader_status=#{map.leaderStatus},
	
	
		first_paper=#{map.firstPaper},
	
	
		create_time=#{map.createTime}
	
	
		zd_table=#{map.zdTablePath}
	
 
WHERE stu_id=#{stuId}

  	update firstpaper 
  		
  			tea_status = #{status, jdbcType=VARCHAR}
  		
  		where id in
  		  
			  #{item}

mapper.select:
    	SELECT distinct COUNT(*) from student s 
    	
    		 s.user_id NOT in (SELECT f.stu_id from firstpaper f)
         	 
	        	AND s.major = #{map.major}
	         
	         
	         	AND s.class = #{map.className}

select count(*) from firstpaper f, student s
f.first_paper is NOT NULL and f.first_paper != "" and f.leader_status = 0 and f.stu_id = s.user_id
AND s.major = #{map.major}
AND s.class = #{map.className}

select count(*) from firstpaper f, student s
f.first_paper is NOT NULL and f.first_paper != "" and f.leader_status = 2 and f.stu_id = s.user_id
AND s.major = #{map.major}
AND s.class = #{map.className}

select count(*) from firstpaper f, student s
f.first_paper is NOT NULL and f.first_paper != "" and f.leader_status = 1 and f.stu_id = s.user_id
AND s.major = #{map.major}
AND s.class = #{map.className}

select fp.* 
from
firstpaper fp

fp.tea_id=#{teaId}
 
 AND fp.tea_status=#{status}
